Scripts.OnMouseUp:
if button == "LeftButton" and self:GetParent().isMoving then
    self:GetParent():StopMovingOrSizing();
    self:GetParent().isMoving = false;
end

Scripts.OnLoad:
self.text:SetText(Sorted.Localize("CONFIG_BEHAVIOR_SMOOTH_SCROLLING_POWER"))
if Sorted_IsShadowlands() then BackdropTemplateMixin.OnBackdropLoaded(self) end
function self:UpdateText() self.valueString:SetText(math.floor(self:GetValue() * 100) / 50) end self:SetMinMaxValues(0, 0.5) _G[self:GetName().."High"]:SetText("") _G[self:GetName().."Low"]:SetText("") _G[self:GetName().."Text"]:SetText("")
