Scripts.OnMouseUp:
if button == "LeftButton" and SortedFrame.isMoving then
    SortedFrame:StopMovingOrSizing();
    SortedFrame.isMoving = false;
end

Scripts.OnLoad:
if Sorted_IsClassic() then self:Hide() return end self:GetHighlightTexture():SetPoint("TOPLEFT", -4, 0) self:GetHighlightTexture():SetPoint("BOTTOMRIGHT", 4, 0) self:GetHighlightTexture():SetVertexColor(1, 0.9, 0.25, 0.6) self:GetNormalTexture():SetPoint("TOPLEFT", self, "TOPRIGHT", -14, -5) self:GetNormalTexture():SetPoint("BOTTOMRIGHT", self, "BOTTOMRIGHT", 0, 0) self:GetPushedTexture():SetPoint("TOPLEFT", self, "TOPRIGHT", -14, -5) self:GetPushedTexture():SetPoint("BOTTOMRIGHT", self, "BOTTOMRIGHT", 0, 0) self:RegisterForClicks("LeftButtonDown")
function Sorted_DeselectEquipmentSet() Sorted_selectedEquipSet = nil SortedEquipmentSetsDropdownButton.text:SetText("|TInterface\\Icons\\Garrison_Building_Armory:16:16:-2:0:64:64:4:60:4:60|t "..Sorted.Localize("DROPDOWN_MENU_EQUIPMENT_SET")) end Sorted_DeselectEquipmentSet()
function Sorted_SelectEquipmentSet(equipSetID) if 0 > equipSetID then Sorted_selectedEquipSet = -1 SortedEquipmentSetsDropdownButton.text:SetText("|cffff1611 "..Sorted.Localize("DROPDOWN_MENU_EQUIPMENT_SET_NONE")) return end local data = Sorted_GetData() if data.equipSets then local v = data.equipSets[equipSetID] SortedEquipmentSetsDropdownButton.text:SetText("|T"..v.icon..":16:16:-2:0:64:64:4:60:4:60|t |cffffffff"..v.name) Sorted_selectedEquipSet = equipSetID end end

self:GetParent().Bg:Hide()
self.Left:Hide()
self.Middle:Hide()
self.Right:Hide()
self.searchIcon:SetVertexColor(0.6, 0.6, 0.6);
self:SetTextInsets(16, 20, 0, 0);
self.Instructions:SetText(SEARCH);
self.Instructions:ClearAllPoints();
self.Instructions:SetPoint("TOPLEFT", self, "TOPLEFT", 16, 0);
self.Instructions:SetPoint("BOTTOMRIGHT", self, "BOTTOMRIGHT", -20, 0);
self.Instructions:SetFontObject(Sorted12Font)
self:SetFontObject(Sorted12Font)
if not Sorted_IsClassic() then
    self:RegisterEvent("GLOBAL_MOUSE_DOWN")
end

_G["SortedFrameSearchBoxClearButton"]:HookScript("OnClick", function(self) 
    Sorted_FilterItems() 
    Sorted_ScrollToTop()
end)

if not Sorted_IsClassic() then
    SmallMoneyFrame_OnLoad(self);
    MoneyFrame_SetType(self, "STATIC");
    MoneyFrame_Update(self, GetReagentBankCost())
    self:RegisterEvent("REAGENTBANK_PURCHASED")
end
