		["Reminder: Time To Die"] = {
			["outline"] = "OUTLINE",
			["wagoID"] = "bzVZr5ODZ",
			["parent"] = "Merfin: Target Info",
			["displayText"] = "%c",
			["customText"] = "function()\n    \n    if WeakAuras.IsOptionsOpen() then return \"00:13\" end\n    local health = UnitHealth(\"target\")\n    local time = GetTime()\n    local text = \"\"\n    local oldhealth\n    if oldhealth ~= health then\n        oldhealth = health\n        if health == UnitHealthMax(\"target\") then\n            aura_env.health0, aura_env.time0, aura_env.mhealth, aura_env.mtime = nil\n            text = \"\"\n            return text\n        end\n        \n        if not aura_env.health0 then\n            aura_env.health0, aura_env.time0 = health, time\n            aura_env.mhealth, aura_env.mtime = health, time\n            return \"\"\n        end\n        \n        aura_env.mhealth = (aura_env.mhealth + health) * .5\n        aura_env.mtime = (aura_env.mtime + time) * .5\n        \n        if aura_env.mhealth >= aura_env.health0 then\n            text = \"\"\n            aura_env.health0, aura_env.time0, aura_env.mhealth, aura_env.mtime = nil\n        else\n            time = health * (aura_env.time0 - aura_env.mtime) / (aura_env.mhealth - aura_env.health0)\n            if (time <= 60) then\n                text = format(\"00:%0.2d\", time)           \n            elseif (time > 60) then\n                text = format(\"%d:%0.2d\", time / 60, time % 60)\n            end\n            return text\n        end\n    end\n    return text\nend",
